mapper.select:
select u.user_name as userName , uo.order_gas as orderGas, uo.order_payment as orderPayment
from user_orders uo join user u on uo.user_id = u.user_id
where uo.order_id = #{orderId}

SELECT MAX(DATE_FORMAT(orders.create_time ,'%Y-%m-%d')) AS createTime, SUM( CASE WHEN orders.user_id IS NOT NULL THEN 1 ELSE 0 END ) AS icCard, SUM(orders.order_gas) AS orderGas, SUM(orders.order_payment) AS orderPayment, any_value(e.`emp_name`) AS empName FROM user_orders orders LEFT JOIN employee e ON orders.employee_id = e.emp_id LEFT JOIN sys_organization org ON e.emp_org_id = org.org_id WHERE orders.usable = 1 AND (account_state NOT IN(1,3) OR account_state IS NULL)
AND orders.user_id = #{userId}
AND org.org_id= #{empOrgId}
AND LOCATE (#{empName} ,e.`emp_name`) > 0
AND DATE_FORMAT(orders.create_time,'%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(orders.create_time,'%Y-%m-%d') <= #{endDate}
GROUP BY DATE_FORMAT(orders.create_time ,'%Y-%m-%d') ORDER BY DATE_FORMAT(orders.create_time ,'%Y-%m-%d') DESC
