mapper.select:
select user_card_id AS userCardId, user_id as userId, card_id as cardId ,card_identifier as cardIdentifier,card_password as cardPassword,
create_time as  createTime, create_by as createBy,update_time as updateTime,update_by as updateBy ,usable from  user_card
where usable=1

        and user_id = #{userId}


        and card_id = #{iccardId}
