mapper.select:
select user_card_id AS userCardId, user_id as userId, card_id as cardId ,card_identifier as cardIdentifier,card_password as cardPassword, order_id as orderId,
create_time as  createTime, create_by as createBy,update_time as updateTime,update_by as updateBy ,usable from  user_card
where usable=1

        and user_id = #{userId}


        and card_id = #{iccardId}

select count(user_card_id) from user_card
where 1=1

    and  user_id <> #{userId} 


    and card_identifier = #{iccardIdentifier}

SELECT COUNT(user_card_id) FROM user_card
WHERE usable=1

    AND user_id = #{userId}


    AND card_id = #{iccardId}


    AND card_identifier=#{iccardIdentifier}
