mapper.select:
SELECT user_lock_id as userLockId,user_id as userId,is_lock as isLock, (CASE WHEN is_lock=1 THEN '锁定' WHEN is_lock=0 THEN '未锁定' ELSE '未锁定' END) AS
lockStatus,lock_reason as lockReason,create_time as createTime
FROM user_lock where user_id = #{userId,jdbcType=INTEGER}

SELECT u.user_id as userId,u.user_name as userName,u.user_phone as userPhone, dist.dist_name as distName,u.user_address as userAddress, u.user_idcard as userIdcard,u.user_deed as userDeed, u.user_type as userType,u.user_gas_type as userGasType, sd.dict_key AS userTypeName, u.user_gas_type as userGasType,sd2.dict_key AS userGasTypeName, u.user_status as userStatus,sd3.dict_key AS userStatusName FROM user u LEFT JOIN user_card card ON u.user_id = card.user_id LEFT JOIN sys_district dist ON u.user_dist_id = dist.dist_id LEFT JOIN sys_dictionary sd ON u.user_type = sd.dict_value AND sd.dict_category = 'user_type' AND sd.usable = 1 LEFT JOIN sys_dictionary sd2 ON u.user_gas_type = sd2.dict_value AND sd2.dict_category = 'user_gas_type' AND sd2.usable = 1 LEFT JOIN sys_dictionary sd3 ON u.user_status = sd3.dict_value AND sd3.dict_category = 'user_status' AND sd3.usable = 1
WHERE u.usable = 1 AND card.usable = 1 AND card.card_status = 1
AND u.user_status = #{userStatus}
AND u.user_name = #{userName}

SELECT u.user_id AS userId, u.user_name AS userName, uc.card_id AS iccardId, uc.card_identifier AS iccardIdentifier, u.user_phone AS userPhone, sd.dist_name as userDistName, u.user_address AS userAddress, uo.totalOrderGas, uo.totalOrderPayment, uo.startBuyDay, uo.endBuyDay, datediff(now(),uo.endBuyDay) AS notBuyDayCount, ROUND(totalOrderGas/(TIMESTAMPDIFF(MONTH,uo.startBuyDay,uo.endBuyDay)+1),1) AS monthAveGas, ROUND(totalOrderPayment /(TIMESTAMPDIFF(MONTH,uo.startBuyDay,uo.endBuyDay)+1),2) AS monthAvePayment FROM `user` u JOIN user_card uc ON u.user_id = uc.user_id AND uc.usable = 1 LEFT JOIN sys_district sd on u.user_dist_id = sd.dist_id AND sd.usable = 1 JOIN ( SELECT user_id, SUM(order_gas) AS totalOrderGas, SUM(order_payment) AS totalOrderPayment, MIN(create_time) AS startBuyDay, MAX(create_time) AS endBuyDay FROM user_orders WHERE account_state NOT IN(1,3) OR account_state IS NULL GROUP BY user_id ) uo ON u.user_id = uo.user_id LEFT JOIN user_meters usermeters ON u.user_id = usermeters.user_id LEFT JOIN meter me ON usermeters.meter_id = me.meter_id WHERE u.usable = 1
AND ROUND(totalOrderGas/(TIMESTAMPDIFF(MONTH,uo.startBuyDay,uo.endBuyDay)+1),1) <= #{monthAveGas}
AND ROUND(totalOrderPayment /(TIMESTAMPDIFF(MONTH,uo.startBuyDay,uo.endBuyDay)+1),2) <= #{monthAvePayment}
AND datediff(now(),uo.endBuyDay) >= #{notBuyDayCount}
AND u.user_dist_id IN (${distIds})
AND u.user_address like CONCAT('%',#{userAddress},'%')
AND LOCATE (#{meterCode} ,me.meter_code) > 0

SELECT u.user_id AS userId, u.user_name AS userName, u.user_phone AS userPhone, u.user_idcard AS userIdcard, u.user_deed AS userDeed, u.user_dist_id AS userDistId, u.user_address AS userAddress, u.user_type AS userType, u.user_gas_type AS userGasType, u.user_status AS userStatus, u.user_locked AS userLocked, u.service_times AS serviceTimes, u.free_gas_flag AS freeGasFlag, u.create_time AS createTime, u.create_by AS createBy, u.update_time AS updateTime, u.update_by AS updateBy, u.usable, u.remarks, uo.totalOrderTimes, uo.totalOrderGas FROM `user` u LEFT JOIN (SELECT user_id, card_identifier, usable FROM user_card WHERE usable = 1) uc ON u.user_id = uc.user_id LEFT JOIN (SELECT user_id, COUNT(order_id) AS totalOrderTimes, SUM(order_gas) AS totalOrderGas, SUM(order_payment) AS totalOrderPayment FROM user_orders WHERE order_status NOT IN (4) GROUP BY user_id) uo ON u.user_id = uo.user_id LEFT JOIN user_meters um ON u.user_id = um.user_id LEFT JOIN meter m ON um.meter_id = m.meter_id WHERE u.usable = 1
AND u.user_id = #{userId}
AND LOCATE (#{userName} ,u.user_name) > 0
AND u.user_type = #{userType}
AND uc.card_identifier = #{cardIdentifier}
AND LOCATE (#{meterCode} ,m.meter_code) > 0
GROUP BY u.user_id ORDER BY u.create_time

SELECT u.user_id, u.user_name, u.user_phone, u.user_idcard, u.user_deed, u.user_dist_id, dist.dist_name as user_dist_name, u.user_address, u.user_type, sd1.dict_key as user_type_name, u.user_gas_type, sd2.dict_key as user_gas_type_name, u.user_status, sd3.dict_key as user_status_name, u.user_locked, m.meter_id, m.meter_code, uc.user_card_id, uc.card_id, uc.card_identifier, u.create_time, u.create_by, u.update_time, u.update_by, u.usable, u.remarks FROM user u left join user_meters um on u.user_id = um.user_id and um.usable = 1 left join meter m on um.meter_id = m.meter_id and m.usable = 1 left join user_card uc on uc.user_id = u.user_id and uc.usable = 1 left join sys_district dist on u.user_dist_id = dist.dist_id and dist.usable = 1 LEFT JOIN sys_dictionary sd1 on u.user_type = sd1.dict_value and sd1.dict_category = 'user_type' and sd1.usable = 1 LEFT JOIN sys_dictionary sd2 on u.user_gas_type = sd2.dict_value and sd2.dict_category = 'user_gas_type' and sd2.usable = 1 LEFT JOIN sys_dictionary sd3 on u.user_status = sd3.dict_value and sd3.dict_category = 'user_status' and sd3.usable = 1 where u.usable = 1
AND u.user_id = #{userId}
AND u.user_dist_id = #{userDistId}
AND u.user_address LIKE CONCAT('%', #{userAddress}, '%')
AND u.user_type = #{userType}
AND u.user_status = #{userStatus}
AND m.meter_code LIKE CONCAT('%', #{meterCode}, '%')
AND uc.card_identifier LIKE CONCAT('%', #{cardIdentifier}, '%')
order by u.user_id DESC

mapper.update:
update user
set
free_gas_flag = #{flag}
where usable = 1 and user_id = #{userId}

mapper.select:
SELECT u.user_id as userId, u.user_name as userName, u.user_phone as userPhone, u.user_idcard as userIdcard, u.user_deed as userDeed, u.user_dist_id as userDistId, u.user_address as userAddress, u.user_type as userType, u.user_gas_type as userGasType, u.user_status as userStatus, u.user_locked as userLocked, u.service_times as serviceTimes, u.free_gas_flag as freeGasFlag, u.create_time as createTime, u.create_by as createBy, u.update_time as updateTime, u.update_by as updateBy, u.usable, u.remarks,uo.totalOrderTimes as totalOrderTimes, uo.totalOrderGas as totalOrderGas FROM `user` u LEFT JOIN (SELECT user_id, COUNT(order_id) AS totalOrderTimes, SUM(order_gas) AS totalOrderGas, SUM(order_payment) AS totalOrderPayment FROM user_orders WHERE order_status NOT IN (4) GROUP BY user_id) uo ON u.user_id = uo.user_id WHERE u.usable = 1
AND u.user_id = #{userId}
AND LOCATE (#{userName} ,u.user_name) > 0
AND u.user_type = #{userType}
ORDER BY u.create_time
