mapper.insert:
insert into sys_dictionary (id, dict_id, dict_key,
dict_value, dict_category, dict_sort,
create_time, create_by, update_time,
update_by, useable, remarks
)
values (#{id,jdbcType=CHAR}, #{dictId,jdbcType=INTEGER}, #{dictKey,jdbcType=VARCHAR},
#{dictValue,jdbcType=VARCHAR}, #{dictCategory,jdbcType=VARCHAR}, #{dictSort,jdbcType=INTEGER},
#{createTime,jdbcType=TIMESTAMP}, #{createBy,jdbcType=BIGINT}, #{updateTime,jdbcType=TIMESTAMP},
#{updateBy,jdbcType=BIGINT}, #{useable,jdbcType=BIT}, #{remarks,jdbcType=VARCHAR}
)
