mapper.update:
update user




                when user_id = #{i.userId} then #{i.updateBy}






                when user_id = #{i.userId} then NOW()






                when user_id =#{i.userId} then 0




where

    user_id =#{i.userId}

mapper.select:
select u.user_id, dist.dist_name, u.user_address, u.user_type, u.user_gas_type, u.user_status, u.user_locked, mt.meter_category, u.create_time, u.create_by, u.update_time, u.update_by, u.usable, u.remarks from user u LEFT JOIN user_meters um ON u.user_id=um.user_id LEFT JOIN meter m ON um.meter_id=m.meter_id LEFT JOIN meter_type mt ON m.meter_type_id=mt.meter_type_id left join sys_district dist on u.user_dist_id = dist.dist_id where u.usable = 1 and um.usable = 1 and m.usable=1
AND u.user_id LIKE CONCAT('%', #{userId,jdbcType=INTEGER}, '%')
AND dist.dist_name = #{distName,jdbcType=INTEGER}
AND u.user_address LIKE CONCAT('%', #{userAddress,jdbcType=INTEGER}, '%')
AND u.user_type = #{userType,jdbcType=INTEGER}
AND u.user_gas_type = #{userGasType,jdbcType=INTEGER}
AND u.user_status = #{userStatus,jdbcType=VARCHAR}

mapper.update:
update user




                when user_id = #{i.userId} then #{i.updateBy}






                when user_id = #{i.userId} then NOW()






                when user_id =#{i.userId} then #{i.userStatus}




where

    user_id =#{i.userId}
